// Task - https://leetcode.com/problems/find-the-difference/
fun main() {
    println(findTheDifference("abcd", "abcde"))
    println(findTheDifference("", "y"))
    println(
        findTheDifference(
            "",
            ""
        )
    )
}

fun findTheDifference(s: String, t: String): Char {
    val l = t.fold(mutableMapOf<Int, Int>()) { acc, char ->
        acc[char.code] = acc[char.code]?.plus(1) ?: 1
        acc
    }
    s.forEach {
        l[it.code] = l[it.code]!! - 1
    }

    return l.filterNot { it.value == 0 }.keys.first().toChar()
}